#!/usr/bin/python# coding=utf-8#Copyright:     #File:          translate_image.py#Author:        Benny Wong#Email:         #Date:          2020-03-17#Description:   批量转换图片import osimport ioimport refrom PIL import ImageFILES_PATH = "."def TranslateImages(src, imageFormat, imageFormatNew):    for root, subdirs, files in os.walk(src):        #文件        for path in files:            if path.endswith(imageFormat):                filePath = os.path.join(root, path)                filePathNew = re.sub(imageFormat, imageFormatNew, filePath)                try:                    img = Image.open(filePath)                    img = img.convert("RGB")                    img.save(filePathNew)                    print("translate image successed %s" % filePathNew)                except:                    print("translate image failed %s" % filePathNew)    # for root, subdirs, files in os.walk(src):    #     for subdir in subdirs:    #         new = re.sub(pattern, replace, subdir)    #         # os.rename(root + "/" + subdir, root + "/" + new)    #         print("subdir %s rename to %s" % (subdir, new))# -------------- main --------------if __name__ == '__main__':    TranslateImages(FILES_PATH, ".png", ".jpg")    os.system("pause")